<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>EduManage - Student Management System | Welcome</title>
    <meta name="description" content="EduManage - Comprehensive Student Management System for educational institutions">
    <meta name="keywords" content="student management, education, enrollment, courses, analytics">
    <meta name="author" content="EduManage System">
    
    <!-- Favicon -->
    <link rel="icon" type="image/svg+xml" href="data:image/svg+xml;base64,">
    <link rel="apple-touch-icon" href="data:image/svg+xml;base64,">
    
    <script src="https://cdn.tailwindcss.com"></script>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
    <style>
        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }
        
        @keyframes slideInLeft {
            from { opacity: 0; transform: translateX(-50px); }
            to { opacity: 1; transform: translateX(0); }
        }
        
        @keyframes slideInRight {
            from { opacity: 0; transform: translateX(50px); }
            to { opacity: 1; transform: translateX(0); }
        }
        
        @keyframes pulse {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.05); }
        }
        
        @keyframes gradient {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
        }
        
        .animate-fadeIn { animation: fadeIn 1s ease-out; }
        .animate-slideInLeft { animation: slideInLeft 1s ease-out; }
        .animate-slideInRight { animation: slideInRight 1s ease-out; }
        .animate-pulse-slow { animation: pulse 2s infinite; }
        .animate-gradient { animation: gradient 3s ease infinite; }
        
        .gradient-bg {
            background: linear-gradient(-45deg, #667eea, #764ba2, #f093fb, #f5576c);
            background-size: 400% 400%;
        }
        
        .loader {
            border: 3px solid #f3f3f3;
            border-top: 3px solid #667eea;
            border-radius: 50%;
            width: 40px;
            height: 40px;
            animation: spin 1s linear infinite;
        }
        
        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }
        
        .progress-bar {
            height: 4px;
            background: linear-gradient(90deg, #667eea, #764ba2);
            animation: progress 3s ease-out forwards;
        }
        
        @keyframes progress {
            from { width: 0%; }
            to { width: 100%; }
        }
    </style>
</head>
<body class="gradient-bg animate-gradient min-h-screen flex items-center justify-center">
    <div class="text-center text-white">
        <!-- Logo Animation -->
        <div class="mb-8 animate-fadeIn">
            <div class="inline-flex items-center justify-center w-24 h-24 bg-white bg-opacity-20 rounded-full backdrop-blur-sm animate-pulse-slow">
                <i class="fas fa-graduation-cap text-5xl text-white"></i>
            </div>
        </div>
        
        <!-- Title Animation -->
        <h1 class="text-5xl font-bold mb-4 animate-slideInLeft">
            EduManage
        </h1>
        
        <!-- Subtitle Animation -->
        <p class="text-xl mb-8 opacity-90 animate-slideInRight" style="animation-delay: 0.3s;">
            Student Management System
        </p>
        
        <!-- Loading Animation -->
        <div class="mb-8 animate-fadeIn" style="animation-delay: 0.6s;">
            <div class="loader mx-auto mb-4"></div>
            <p class="text-sm opacity-75" id="loading-text">Initializing...</p>
        </div>
        
        <!-- Progress Bar -->
        <div class="w-64 mx-auto bg-white bg-opacity-20 rounded-full overflow-hidden animate-fadeIn" style="animation-delay: 0.9s;">
            <div class="progress-bar"></div>
        </div>
        
        <!-- Features Animation -->
        <div class="mt-12 grid grid-cols-3 gap-8 max-w-2xl mx-auto animate-fadeIn" style="animation-delay: 1.2s;">
            <div class="text-center opacity-75 hover:opacity-100 transition-opacity">
                <i class="fas fa-user-graduate text-3xl mb-2"></i>
                <p class="text-sm">Student Management</p>
            </div>
            <div class="text-center opacity-75 hover:opacity-100 transition-opacity">
                <i class="fas fa-book text-3xl mb-2"></i>
                <p class="text-sm">Course Management</p>
            </div>
            <div class="text-center opacity-75 hover:opacity-100 transition-opacity">
                <i class="fas fa-chart-line text-3xl mb-2"></i>
                <p class="text-sm">Analytics</p>
            </div>
        </div>
    </div>
    
    <script>
        // Loading text animation
        const loadingTexts = [
            'Initializing...',
            'Loading resources...',
            'Preparing dashboard...',
            'Almost ready...',
            'Welcome!'
        ];
        
        let textIndex = 0;
        const loadingTextElement = document.getElementById('loading-text');
        
        const updateLoadingText = () => {
            loadingTextElement.textContent = loadingTexts[textIndex];
            textIndex = (textIndex + 1) % loadingTexts.length;
        };
        
        // Update loading text every 600ms
        const textInterval = setInterval(updateLoadingText, 600);
        
        // Redirect to dashboard after 3.5 seconds
        setTimeout(() => {
            clearInterval(textInterval);
            loadingTextElement.textContent = 'Redirecting...';
            
            // Add fade out effect
            document.body.style.transition = 'opacity 0.5s ease-out';
            document.body.style.opacity = '0';
            
            // Redirect to dashboard
            setTimeout(() => {
                window.location.href = '/dashboard';
            }, 500);
        }, 3500);
        
        // Add some interactive effects
        document.addEventListener('DOMContentLoaded', () => {
            // Add hover effect to features
            const features = document.querySelectorAll('.grid > div');
            features.forEach((feature, index) => {
                feature.style.animationDelay = `${1.2 + (index * 0.1)}s`;
            });
        });
    </script>
</body>
</html>
